import React from 'react'


const MenuCard = ({menuData}) => {
  return (
    <>
    <section className='main-card--cointainer'>
      { menuData.map((curElem)=>{
        return(
          <>
          <div className='card-container' key={curElem.id}>
            <div className='card'>
              <div className='card-body'>
                <span className='card-number card-circle subtle'>{curElem.id}</span>
                <span className='card-author subtle'>{curElem.category}</span>
                <h2 className='card-title'> {curElem.name}</h2>
                <span className='card-description subtle'> Maggi noodles are dried noodles fused with oil, and sold with a packet of flavorings. These noodles are usually eaten after being cooked in boiling water for 3 to 5 minutes or eaten straight from the packet. Just boil the water, add the tastemaker and noodles and your maggi is ready within 2 to 3 minutes.</span>
                <span className='card-read'> Read </span>

              </div>
              <img src='{curElem.image}' alt='image' className='card-media' />
              <span className='card-tag subtle' > Order now</span>
            </div>
          </div>
          </>
        )
      })}

    </section>

    </>
  )
}

export default MenuCard;
